{
    "clientId": "general-user-client",
    "enabled": true,
    "redirectUris": [
        "{{CLIENT_BASE_URL}}/api/auth/callback/general-user-keycloak-client"
    ],
    "webOrigins": [
        "{{WEB_ORIGIN}}"
    ],
    "publicClient": false,
    "standardFlowEnabled": true,
    "directAccessGrantsEnabled": true,
    "frontchannelLogout": true,
    "authenticationFlowBindingOverrides": {
        "browser": "{{TEMP_AUTH_FLOW_ID}}"
    },
    "protocolMappers": [
        {
            "name": "userType",
            "protocol": "openid-connect",
            "protocolMapper": "oidc-hardcoded-claim-mapper",
            "consentRequired": false,
            "config": {
                "introspection.token.claim": "true",
                "claim.value": "general",
                "userinfo.token.claim": "true",
                "id.token.claim": "true",
                "access.token.claim": "true",
                "claim.name": "userType",
                "jsonType.label": "String",
                "access.tokenResponse.claim": "false"
            }
        }
    ],
    "attributes": {
        "policyUri": "{{CLIENT_BASE_URL}}/terms/privacy-policy",
        "tosUri": "{{CLIENT_BASE_URL}}/terms/use"
    }
}

{
  "attributes": [
    {
      "name": "username",
      "displayName": "${username}",
      "validations": {
        "length": {
          "min": 3,
          "max": 255
        },
        "username-prohibited-characters": {},
        "up-username-not-idn-homograph": {}
      },
      "annotations": {},
      "permissions": {
        "view": [
          "admin",
          "user"
        ],
        "edit": [
          "admin"
        ]
      }
    },
    {
      "name": "email",
      "displayName": "${email}",
      "validations": {
        "email": {},
        "length": {
          "max": 255
        }
      },
      "annotations": {},
      "required": {
        "roles": [
          "user"
        ]
      },
      "permissions": {
        "view": [
          "admin",
          "user"
        ],
        "edit": []
      }
    },
    {
      "name": "firstName",
      "displayName": "${firstName}",
      "validations": {
        "length": {
          "max": 255
        },
        "person-name-prohibited-characters": {}
      },
      "annotations": {},
      "permissions": {
        "view": [
          "admin",
          "user"
        ],
        "edit": []
      }
    },
    {
      "name": "lastName",
      "displayName": "${lastName}",
      "validations": {
        "length": {
          "max": 255
        },
        "person-name-prohibited-characters": {}
      },
      "annotations": {},
      "permissions": {
        "view": [
          "admin",
          "user"
        ],
        "edit": []
      }
    },
    {
      "name": "nickname",
      "displayName": "${profile.attribute.nickname}",
      "validations": {
        "pattern": {
          "pattern": "[a-zA-Z0-9-_]+",
          "error-message": "profile.error.nickname"
        }
      },
      "annotations": {},
      "required": {
        "roles": [
          "admin",
          "user"
        ]
      },
      "permissions": {
        "view": [],
        "edit": [
          "admin",
          "user"
        ]
      }
    },
    {
      "name": "prefecture",
      "displayName": "${profile.attribute.prefecture}",
      "validations": {
        "options": {
          "options": {{prefecture}}
        }
      },
      "annotations": {
        "inputType": "select"
      },
      "required": {
        "roles": [
          "user"
        ]
      },
      "permissions": {
        "view": [],
        "edit": [
          "user"
        ]
      }
    },
    {
      "name": "city",
      "displayName": "${profile.attribute.city}",
      "validations": {
        "options": {
          "options": {{city}}
        }
      },
      "annotations": {
        "inputType": "select"
      },
      "required": {
        "roles": [
          "user"
        ]
      },
      "permissions": {
        "view": [],
        "edit": [
          "user"
        ]
      }
    },
    {
      "name": "age",
      "displayName": "${profile.attribute.age}",
      "validations": {
        "options": {
          "options": [
            "10代以下",
            "20代",
            "30代",
            "40代",
            "50代",
            "60代以上",
            "その他"
          ]
        }
      },
      "annotations": {
        "inputType": "select"
      },
      "required": {
        "roles": [
          "user"
        ]
      },
      "permissions": {
        "view": [],
        "edit": [
          "user"
        ]
      }
    },
    {
      "name": "gender",
      "displayName": "${profile.attribute.gender}",
      "validations": {
        "options": {
          "options": [
            "男性",
            "女性",
            "その他",
            "回答しない"
          ]
        }
      },
      "annotations": {
        "inputType": "select"
      },
      "required": {
        "roles": [
          "user"
        ]
      },
      "permissions": {
        "view": [],
        "edit": [
          "user"
        ]
      }
    }
  ],
  "groups": []
}
